/*Tanay Biradar*///Define servo#define ECHO A4#define TRIG A5#include <Servo.h> Servo rangerServo;  //Drive constants#define LEFTTURNTIME 250   //Set according to calibration#define RIGHTTURNTIME 260#define ENA 5                 // pins to enable drives  ENA = right side#define ENB 11                // ENB - left side#define IN1 6                 // H-bridge pins#define IN2 7                 // IN1, IN2 are right side#define IN3 8                 // IN3, IN4 are left#define IN4 9//Function Prototypesvoid allStop();void forward();void back();void right();void left();  int findRange(){  //Returns distance in cm  int range;  digitalWrite(TRIG,LOW); //Turns off ultrasonic pulse  delayMicroseconds(2);  digitalWrite(TRIG, HIGH); //Turns on ultrasonic pulse  delayMicroseconds(20);  digitalWrite(TRIG, LOW);  range = pulseIn(ECHO,HIGH); //Pulse width measure in difference of microseconds  range = range/58; //Microseconds per cm  return(range);}void setup() {  Serial.begin(9600);     // start serial port, 9600 baud  pinMode(ECHO, INPUT);  pinMode(TRIG, OUTPUT);  pinMode(ENA, OUTPUT);   pinMode(ENB, OUTPUT);  pinMode(IN1, OUTPUT);  pinMode(IN2, OUTPUT);  pinMode(IN3, OUTPUT);  pinMode(IN4, OUTPUT);  int servoR = 20;  int servoL = 180;  int servoC = 110;  rangerServo.attach(3);  /*  for (int i=1;i<=5;i++){    forward();    delay(400);    allStop();    delay(500);  }  */    for (int i=1; i< 100; i++) {    int range;    int range2;  //compare ranges    rangerServo.write(servoC);    delay(500);    range = findRange();    if (range > 30) {      forward();      delay(400);      allStop();      rangerServo.write(servoC);      delay(500);    }    else{      rangerServo.write(servoR);      delay(500);      range = findRange();      rangerServo.write(servoL);      delay(500);      range2 = findRange();      if (range > range2){        right();        delay(RIGHTTURNTIME);        allStop();      }      else{        left();        delay(LEFTTURNTIME);        allStop();      }    }  }}void loop() {    }//Drive functions belowvoid forward(){  analogWrite(ENA, 237);  analogWrite(ENB, 255);  digitalWrite(IN1, HIGH);  digitalWrite(IN2, LOW);  digitalWrite(IN3, LOW);  digitalWrite(IN4, HIGH);}void back(){  digitalWrite(ENA, HIGH);  digitalWrite(ENB, HIGH);  digitalWrite(IN1, LOW);  digitalWrite(IN2, HIGH);  digitalWrite(IN3, HIGH);  digitalWrite(IN4, LOW);}void left(){  digitalWrite(ENA, HIGH);  digitalWrite(ENB, HIGH);  digitalWrite(IN1, HIGH);  digitalWrite(IN2, LOW);  digitalWrite(IN3, HIGH);  digitalWrite(IN4, LOW); }void right(){  digitalWrite(ENA, HIGH);  digitalWrite(ENB, HIGH);  digitalWrite(IN1, LOW);  digitalWrite(IN2, HIGH);  digitalWrite(IN3, LOW);  digitalWrite(IN4, HIGH);} void allStop(){   digitalWrite(ENA, LOW);   digitalWrite(ENB, LOW);} 